import React from 'react';
import {Table, TableHeader, TableRow, TableBody, TableCell, TableHead} from 'carbon-components-react'
import { Button, Loading, InlineLoading, FormGroup, TextInput } from 'carbon-components-react';
import axios from 'axios'
import ReactJson from 'react-json-view'
import _ from 'lodash'

class Landing extends React.Component {
    constructor(){
        super()
        this.state = {
            headers:[
                {
                    key:"seqNo",
                    header:"Seq. No"
                },
                // {
                //     key:"messageId",
                //     header:"Message ID"
                // },
                {
                    key:"correlationId",
                    header:"Correlation ID"
                },
                {
                    key:"message",
                    header:"Message"
                }
            ],
            seqNo:1,
            data:[],
            loading: false, 
            messageLoading:{}
        }
    }

    componentDidMount = ()=>{
        // axios.get('http://localhost:8080/getMessageList?q=testQ').then(async res=>{
        //     console.log(res.data.data)
        //     var msg = res.data.data.messages
        //     console.log(msg)
        //     var arr = await msg.map(item =>{
        //         item.message = 
        //             <div>
        //                 {this.state.messageLoading[item.messageId]?
        //                     <InlineLoading description="Loading..." />
        //                     :
        //                     <Button style={{margin:".5rem"}} onClick={()=>this.getMessage(item.messageId)}>Fetch</Button>
        //                 }
        //             </div>
        //         return item
        //     })
        //     this.setState({data: arr, loading: false})
        // })
    }

    handleChange = (e)=>{
        const { name, value } = e.target
        // console.log(name, value)
        this.setState({
            [name]: value
        })
    }

    getStatus = ()=>{
        return <Button></Button>
    }

    handleSyncSubmit = (e)=>{

        let correlationId = _.times(48, () => (Math.random()*0xF<<0).toString(16)).join('');
        e.preventDefault()
        const data = {
            message: { container_num: this.state.input },
            q:"testQ",
            correlationId: correlationId
        }
        axios.post(`http://localhost:8080/postMessage`, data)
        .then(res=>{
            console.log(res.data)
            // alert("Data submitted with submission ID: " + res.data.messageId)
            var arr = this.state.data
            const newData = {
                seqNo: this.state.seqNo,
                // messageId: res.data.messageId, 
                correlationId: correlationId,
                message: this.getStatus
            }
            arr.push(newData)
            this.setState({input:"", seqNo: newData.seqNo+1})
        })


        // console.log(this.state.input)
        // const data = this.state.data
        // const filtered = data.containers.filter(item =>item.container_num === this.state.sync)
        // console.log(filtered)
        // this.setState({toDisplay: filtered[0]})
    }

    render(){
      return (
        this.state.loading?
        <div>
            <Loading
            description="Active loading indicator" withOverlay={false}
            />        
        </div>
          : 
        <div>
            <div>
            <FormGroup legendText="Sync Call" style={{ minWidth: '400px' }}>
                <TextInput
                id="one"
                name="input"
                value={this.state.input}
                labelText="Enter Container Num"
                onChange={this.handleChange}
                placeholder="Enter Container Number"
                style={{ marginBottom: '1rem' }}
                />
                <Button onClick={this.handleSyncSubmit}>Submit</Button>
            </FormGroup>
            </div>
            <div style={{width:"50rem", textAlign:"left"}}>
                {this.state.toDisplay?
                    <ReactJson 
                        src={this.state.toDisplay} 
                        theme="solarized"
                    />
                :
                <></>
                }
            </div>
            <Table>
                <TableHead>
                    <TableRow>
                    {this.state.headers.map((header) => (
                        <TableHeader key={header.key}>{header.header}</TableHeader>
                    ))}
                    </TableRow>
                </TableHead>
                <TableBody>
                    {this.state.data.map((row) => (
                    <TableRow key={row.key}>
                        {Object.keys(row)
                        .map((key) => {
                            return <TableCell key={key}>{row[key]}</TableCell>;
                        })}
                        <TableCell key={key}>{button}</TableCell>
                    </TableRow>
                    ))}
                </TableBody>
            </Table>
        </div>
      )
    }
};
export default Landing;
